import weka.classifiers.Classifier;
import weka.classifiers.Evaluation;
import weka.classifiers.trees.J48;
import weka.core.Instance;
import weka.core.Instances;

import java.util.Arrays;

/**
 * Created by alex on 29/11/14.
 */
public class OVO3WayDecisionTree extends OVO3WayWekaClassifier {


    @Override
    public Instances[] convertInstances(Instances allInstances) {
        return new Instances[] {
                new BitStringChromosome(fMCIxAD).getFeatureSubset(allInstances),
                new BitStringChromosome(fMCIxHC).getFeatureSubset(allInstances),
                new BitStringChromosome(fHCxAD).getFeatureSubset(allInstances)
        };
    }

    public OVO3WayDecisionTree(DataReader dataReader, Instances allInstances) {
        super(dataReader, allInstances, fMCIxAD, fMCIxHC, fHCxAD);
        Arrays.fill(models, new J48());
    }

    static String fMCIxAD = "";
    static String fMCIxHC = "";
    static String fHCxAD = "";


}
